import React, { useState, createContext, useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/pages/Header';
import Cart from './components/pages/Cart';
import Profile from './components/pages/Profile';
import Profile_admin from './components/pages/Profile_admin';
import User from './models/user';

// Создание контекста для аутентификации
const AuthContext = createContext();

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = async (username, password) => {
    try {
      // Проверка аутентификации пользователя в базе данных
      const user = await User.findOne({ where: { username, password } });
      if (user) {
        setIsAuthenticated(true);
      } else {
        console.log('Неверные учетные данные');
      }
    } catch (error) {
      console.log('Ошибка аутентификации', error);
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, handleLogin, handleLogout }}>
      <Router>
        <Routes>
          <Route path="/" element={<><Header /></>} />
          <Route path="/profile" element={isAuthenticated ? <Profile /> : <Navigate to="/" />} />
          <Route path="/cart" element={isAuthenticated ? <Cart /> : <Navigate to="/" />} />
          <Route path="/admin" element={isAuthenticated ? <Profile_admin /> : <Navigate to="/" />} />
        </Routes>
      </Router>
    </AuthContext.Provider>
  );
}

// Хук для использования контекста аутентификации в других компонентах
export function useAuth() {
  return useContext(AuthContext);
}

export default App;
