import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getOrdersByUser, createOrder, findOrder } from './api';

export default function Profile() {
  const navigate = useNavigate();
  const [OrderTables, setOrderTables] = useState([]);
  const [error, setError] = useState(null);
  const [orderid, setOrderid] = useState(0);
  const username = localStorage.getItem('username');

  const goToHome = () => {
    navigate('/'); // Путь к домашней странице
  };

  const goToCart = () => {
    navigate('/cart'); // Путь к странице корзины
  };

  useEffect(() => {
    const fetchOrderTables = async () => {
      try {
        if (username) {
          const OrderTablesData = await getOrdersByUser(username); // Передаем username
          setOrderTables(OrderTablesData);
        } else {
          setError('Пользователь не авторизован');
        }
      } catch (err) {
        setError(`Ошибка: ${err.message}`);
      }
    };
    fetchOrderTables();
  }, [username]);

  const handleNewOrder = async () => {
    const min = 10;
    const max = 99;
    let isUnique = false;
    let newid = 0;

    do {
      newid = Math.floor(Math.random() * (max - min + 1)) + min;
      const existingID = await findOrder(newid);
      if (!existingID) {
        isUnique = true;
      }
    } while (!isUnique);
    setOrderid(newid);
    console.log(orderid);
    try {
      const newOrder = await createOrder(orderid, username);
      setOrderTables(newOrder);
      console.log(newOrder);
      navigate('/cart');
    } catch (error) {
      setError(`Ошибка при создании заказа: ${error.message}`);
    }
  }

  return (
    <div>
      <h1 className='heading'>Профиль пользователя</h1>
      <h2>Имя пользователя: {username}</h2>
      <div className='container'>
        <div className="buttons-profile">
          <button onClick={handleNewOrder}>
            Новый заказ
          </button>
        </div>
        {error ? (<p>{error}</p>) : (
          <ul>
            <br></br>
            <strong>ID заказа</strong>, <strong>Дата заказа</strong>, <strong>Статус заказа</strong>
            {OrderTables.map(order => (
              <li key={order.orderid}>{order.orderid}, {order.orderdate}, {order.orderstatus}</li>
            ))}
          </ul>
        )}
      </div>
      <div className="navbar">
        <div className="thq-navbar-nav">
          <span>© Здоровье в корзине </span>
          <button className='back' onClick={goToHome}>
            Выйти
          </button>
        </div>
      </div>
    </div>
  );
}
