import axios from 'axios';

const API_URL = 'http://localhost:3001/api'; // Замените на ваш URL API

export const getOrdersByUser = async () => {
  try {
    const response = await axios.get(`${API_URL}/ordertables`);
    return response.data;
  } catch (error) {
    console.error('Error fetching orders:', error);
    throw error;
  }
};

export const login = async (username, password) => { // Добавляем параметры
  try {
    const response = await axios.post(`${API_URL}/login`, {
      username,
      password
    });
    if (response.status === 200) {
      localStorage.setItem('currentUser', username); // Сохраняем имя в localStorage
      return response.data;
    } else {
      return response;
    }
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};
