import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Header = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/login', {
        username,
        password
      });

      if (response.status === 200) {
        navigate('/profile');
      } else {
        setError(response.data.message); 
      }
    } catch (error) {
      setError('Ошибка авторизации. Проверьте имя пользователя и пароль.');
    }
  };

  return (
    <header>
      <div className='heading'>Здоровье в корзине</div>
      <h1 className='heading'>Авторизация</h1>
      <div className='container'>
        <div className='login'>
          <div className='form'>
            <form>
              {error && <p className="error">{error}</p>} {/* Отображаем ошибку, если она есть */}
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Имя пользователя"
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Пароль"
              />
              <button onClick={handleLogin}>
                Войти
              </button>
            </form>
          </div>
        </div>
      </div>
      <div class="navbar">
        <div class="thq-navbar-nav">
          <span>Журавская Е.В.<br></br>Теплякова Ю.Д.</span>
        </div>
      </div>
    </header>
  );
}

export default Header;
