import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { createOrder, allMedicines } from './api';

export default function Cart() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [error, setError] = useState(null);
  const [OrderTables, setOrderTables] = useState();
  const [Medicines, setMedicines] = useState([]);
  const [quantities, setQuantities] = useState({});

  const goToProfile = () => {
    navigate('/profile'); // Путь к странице Cart.js
  };

  const updateQuantity = (medicineId, newQuantity) => {
    if (newQuantity >= 0) {
      setQuantities((prevQuantities) => ({
        ...prevQuantities,
        [medicineId]: newQuantity,
      }));
    }
  };

  useEffect(() => {
    const handleNewOrder = async () => {
      try {
        const newOrder = await createOrder(username);
        setOrderTables(newOrder); // Добавьте новый заказ в список
        console.log(username);
        console.log(newOrder);
      } catch (error) {
        setError(`Ошибка при создании заказа: ${error.message}`);
      }
    }
    handleNewOrder();
    const showallMedicines = async () => {
      try {
        const medicine = await allMedicines();
        setMedicines(medicine);
      } catch (error) {
        setError(`Ошибка при выводе лекарств: ${error.message}`)
      }
    }
    showallMedicines();
  }, [username]);

  return (
    <div>
      <h1 className='heading'>Ваша Корзина</h1>
      <div className='container'>
        <ul>
          <table className='table'>
            <thead>
              <tr>
                <th>Название товара</th>
                <th>Цена</th>
                <th>Количество</th>
              </tr>
            </thead>
            {error ? (
              <tbody><p>{error}</p></tbody>
            ) : (
              Medicines.map(med => (
                <tbody key={med.medicineid}>
                  <tr>
                    <td>{med.medicineid}</td>
                    <td>{med.price}</td>
                    <td>
                      <input
                        type="number"
                        value={quantities[med.medicineid] || 0}
                        onChange={(e) => updateQuantity(med.medicineid, e.target.value)}
                      />
                    </td>
                  </tr>
                </tbody>
              ))
            )}
            <tfoot>
              <tr>
                <th scope="row" colSpan="3">Общая сумма: </th>
                <td>
                  <button className="btn" onClick={goToProfile}>
                    Оформить заказ
                  </button></td>
              </tr>
            </tfoot>

          </table>
        </ul>
      </div>
      <div className="navbar">
        <div className="thq-navbar-nav">
          <span>© Здоровье в корзине</span>
          <button className="back" onClick={goToProfile}>
            Назад в профиль
          </button>
        </div>
      </div>
    </div>
  )
}
