import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { createOrder } from './api';

export default function Cart() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [error, setError] = useState(null);
  const [OrderTables, setOrderTables] = useState([]);

  const goToProfile = () => {
    navigate('/profile'); // Путь к странице Cart.js
  };

  useEffect(() => {
    const handleNewOrder = async (username) => {
      try {
        const newOrder = await createOrder(username);
        setOrderTables(newOrder);
      } catch (error) {
        setError(`Ошибка при создании заказа: ${error.message}`);
      }
    }
    handleNewOrder(username);
  }, [username]);

  function addValue() {
    var cell = document.getElementById("cell");
    var currentValue = parseInt(cell.innerHTML);
    var newValue = currentValue + 1;
    cell.innerHTML = newValue;
  }
  function minusValue() {
    var cell = document.getElementById("cell");
    var currentValue = parseInt(cell.innerHTML);
    if (currentValue > 0) {
      var newValue = currentValue - 1;
      cell.innerHTML = newValue;
    }
  }
  return (

    <div>
      <h1 className='heading'>Ваша Корзина</h1>
      <div className='container'>
        <table className='table'>
          <ul>
            <thead>
              <tr>
                <th>Название товара</th>
                <th>Цена</th>
                <th>Количество</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Товар</td>
                <td>Себестоимость</td>
                <td id="cell">0</td>
                <td className='td'>
                  <button onClick={addValue}>+</button>
                  <button onClick={minusValue}>-</button>
                </td>
              </tr>
            </tbody>
            <tfoot>
              <tr>
                <th scope="row" colspan="3">Общая сумма: </th>
                <td>
                  <button class="btn" onClick={goToProfile}>
                    Оформить заказ
                  </button></td>
              </tr>
            </tfoot>
          </ul>
        </table>

      </div>
      <div class="navbar">
        <div class="thq-navbar-nav">
          <span>© Здоровье в корзине</span>
          <button className="back" onClick={goToProfile}>
            Назад в профиль
          </button>
        </div>
      </div>
    </div>
  )
}
