import axios from 'axios';

const API_URL = 'http://localhost:3001/api'; // Замените на ваш URL API

export const getOrders = async () => {
  try {
    const response = await axios.get(`${API_URL}/orderItem`);
    return response.data;
  } catch (error) {
    console.error('Error fetching orders:', error);
    throw error;
  }
};

export const login = async (username, password) => { // Добавляем параметры
  try {
    const response = await axios.post(`${API_URL}/login`, { // Используем `/login` endpoint
      username,
      password
    });
    console.log(response.data.message);
    return response.data; 
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};
