import axios from 'axios';

const API_URL = 'http://localhost:3001/api'; 

export const getOrdersByUser = async (username) => { 
    try {
        if (!username) {
            throw new Error('Пользователь не авторизован');
        }
        const url = `${API_URL}/ordertables/${username}`;
        const response = await axios.get(url); 
        return response.data;
    } catch (error) {
        console.error('Error fetching orders:', error);
        throw error;
    }
};

export const createOrder = async (username) => {
  try {
      const response = await axios.post(`${API_URL}/ordertables`, username);
      return response.data;
  } catch (error) {
      console.error('Error creating order:', error);
      throw error;
  }
};

export const login = async (username, password) => { // Добавляем параметры
  try {
    const response = await axios.post(`${API_URL}/login`, {
      username,
      password
    });
    if (response.status === 200) {
      localStorage.setItem('username', username); // Сохраняем имя в localStorage
    }
    return response;
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};
