import axios from 'axios';

const API_URL = 'http://localhost:3001/api'; 

export const getOrdersByUser = async (username) => {
  try {
    if (!username) {
      throw new Error('Пользователь не авторизован');
    }
    //const response = await axios.get(`${API_URL}/ordertables`, {currentUser});
    const response = await axios.get(`${API_URL}/ordertables`, findAll({
            where: {
                username: username // Используйте переданный параметр username
            }
        }));
    return response;
  } catch (error) {
    console.error('Error fetching orders:', error.message);
    throw error;
  }
};

export const login = async (username, password) => { // Добавляем параметры
  try {
    const response = await axios.post(`${API_URL}/login`, {
      username,
      password
    });
    if (response.status === 200) {
      localStorage.setItem('currentUser', username); // Сохраняем имя в localStorage
    }
    return response;
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
};
