import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { createOrder, allMedicines } from './api';

export default function Cart() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [error, setError] = useState(null);
  const [OrderTables, setOrderTables] = useState();
  const [Medicines, setMedicines] = useState([]);

  const goToProfile = () => {
    navigate('/profile'); // Путь к странице Cart.js
  };

  function addValue() {
    var cell = document.getElementById("cell");
    var currentValue = parseInt(cell.innerHTML);
    var newValue = currentValue + 1;
    cell.innerHTML = newValue;
  }

  function minusValue() {
    var cell = document.getElementById("cell");
    var currentValue = parseInt(cell.innerHTML);
    if (currentValue > 0) {
      var newValue = currentValue - 1;
      cell.innerHTML = newValue;
    }
  }

  useEffect(() => {
    const handleNewOrder = async () => {
      try {
        const newOrder = await createOrder(username);
        setOrderTables(newOrder); // Добавьте новый заказ в список
        console.log(username);
        console.log(newOrder);
      } catch (error) {
        setError(`Ошибка при создании заказа: ${error.message}`);
      }
    }
    handleNewOrder();
    const showallMedicines = async () => {
      try {
        const medicine = await allMedicines();
        setMedicines(medicine);
      } catch (error) {
        setError(`Ошибка при выводе лекарств: ${error.message}`)
      }
    }
    showallMedicines();
  }, [username]);

  return (
    <div>
      <h1 className='heading'>Ваша Корзина</h1>
      <div className='container'>
        <ul>
          <table className='table'>
            <tbody>
              <tr>
                {error ? (
                  <td colSpan="2"><p>{error}</p></td>
                ) : (
                  <>
                    <tr>
                      <th>Название</th>
                      <th>Цена</th>
                    </tr>
                    {Medicines.map(med => (
                      <tr key={med.medicineid}>
                        <td>{med.medicineid}</td>
                        <td>{med.price}</td>
                      </tr>
                    ))}
                    <tr>
                      <td colSpan="2">
                        <table style={{ width: '100%' }}>
                          <tr>
                            <td colSpan="2" style={{ textAlign: 'center' }} id="cell">0</td>
                          </tr>
                          <tr>
                            <td className='td'>
                              <button onClick={addValue}>+</button>
                            </td>
                            <td className='td'>
                              <button onClick={minusValue}>-</button>
                            </td>
                          </tr>
                        </table>
                      </td>
                    </tr>
                  </>
                )}
              </tr>


            </tbody>
            <tfoot>
              <tr>
                <th scope="row" colSpan="3">Общая сумма: </th>
                <td>
                  <button className="btn" onClick={goToProfile}>
                    Оформить заказ
                  </button></td>
              </tr>
            </tfoot>

          </table>
        </ul>
      </div>
      <div className="navbar">
        <div className="thq-navbar-nav">
          <span>© Здоровье в корзине</span>
          <button className="back" onClick={goToProfile}>
            Назад в профиль
          </button>
        </div>
      </div>
    </div>
  )
}
