import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { createOrder, allMedicines } from './api';

export default function Cart() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [error, setError] = useState(null);
  const [OrderTables, setOrderTables] = useState();
  const [Medicines, setMedicines] = useState([]);

  const goToProfile = () => {
    navigate('/profile'); // Путь к странице Cart.js
  };

  function addValue() {
    var cell = document.getElementById("cell");
    var currentValue = parseInt(cell.innerHTML);
    var newValue = currentValue + 1;
    cell.innerHTML = newValue;
  }

  function minusValue() {
    var cell = document.getElementById("cell");
    var currentValue = parseInt(cell.innerHTML);
    if (currentValue > 0) {
      var newValue = currentValue - 1;
      cell.innerHTML = newValue;
    }
  }

  useEffect(() => {
    const handleNewOrder = async () => {
      try {
        const newOrder = await createOrder(username);
        setOrderTables(newOrder); // Добавьте новый заказ в список
        console.log(username);
        console.log(newOrder);
      } catch (error) {
        setError(`Ошибка при создании заказа: ${error.message}`);
      }
    }
    handleNewOrder();
    const showallMedicines = async () => {
      try {
        const medicine = await allMedicines();
        setMedicines(medicine);
      } catch (error) {
        setError(`Ошибка при выводе лекарств: ${error.message}`)
      }
    }
    showallMedicines();
  }, [username]);

  return (
    <div>
      <h1 className='heading'>Профиль пользователя</h1>
      <h2>Имя пользователя: {username}</h2>
      <div className='container'>
        <div className="buttons-profile">
          <button onClick={goToCart}>
            Новый заказ
          </button>
        </div>
        {error ? (<p>{error}</p>) : (
          <table>
            <thead>
              <tr>
                <th>Название товара</th>
                <th>Цена</th>
              </tr>
            </thead>
            <tbody>
              {Medicines.map(med => (
                <tr key={med.medicineid}>
                  <td>{med.medicineid}</td>
                  <td>{med.price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
      <div className="navbar">
        <div className="thq-navbar-nav">
          <span>© Здоровье в корзине </span>
          <button className='back' onClick={goToHome}>
            Выйти
          </button>
        </div>
      </div>
    </div>
    );
  
}
