import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getOrdersByUser } from './api'; // Предполагается, что у вас есть функция getOrders

export default function Profile() {
  const navigate = useNavigate();
  const [OrderTables, setOrderTables] = useState([]);
  const [error, setError] = useState(null);

  const goToHome = () => {
    navigate('/'); // Путь к домашней странице
  };

  const goToCart = () => {
    navigate('/cart'); // Путь к странице корзины
  };

  useEffect(() => {
    const fetchOrderTables = async (e) => {
      try {
        const OrderTablesData = await getOrdersByUser();
        setOrderTables(OrderTablesData);
      } catch (err) {
        setError(`Ошибка: ${err.message}`);
      }
    };
    fetchOrderTables();
  }, []);

  return (
    <div>
      <h1 className='heading'>Профиль пользователя</h1>
      <h2>Имя пользователя:</h2>
      <div className='container'>
        <div className="buttons-profile">
          <button onClick={goToCart}>
            Новый заказ
          </button>
        </div>
        {error ? (
          <p>{error}</p>
        ) : (
          <ul>
            {OrderTables.map(order => (
              <li key={order.orderid}>{order.orderid}</li>
            ))}
          </ul>
        )}
      </div>
      <div className="navbar">
        <div className="thq-navbar-nav">
          <span>© Здоровье в корзине </span>
          <button className='back' onClick={goToHome}>
            Выйти
          </button>
        </div>
      </div>
    </div>
  );
}
